import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-missiondetails',
  templateUrl: './missiondetails.component.html',
  styleUrls: ['./missiondetails.component.css']
})
export class MissiondetailsComponent implements OnInit {
  launch: any;

  constructor(private http: HttpClient, private activatedRoute: ActivatedRoute) {

  }

  ngOnInit(): void {
    this.activatedRoute.paramMap.subscribe(params => {
      this.http.get(`https://api.spacexdata.com/v3/launches/${params.get('flight_number')}`).subscribe(response => {
        this.launch = response;
        console.log(response);
      });
    });
  }
}
<h1 class="text-center p-5 text-5xl">SpaceX Mission Details</h1>
<div class="max-w-sm rounded overflow-hidden shadow-lg bg-gray-800 m-4">
    <img class="w-full" src="{{launch.links.mission_patch_small}}" alt="Mission Patch">
    <div class="px-6 py-4">
        <div class="font-bold text-xl text-white mb-2">Mission - {{launch.mission_name}}</div>
        <p class="text-white text-base">Launch Year: {{launch.launch_year}}</p>
        <br>
        <div class="text-white">
            <h3 class="text-lg font-bold">Rocket</h3>
            <p>Name: {{launch.rocket.rocket_name}}</p>
            <p>Type: {{launch.rocket.rocket_type}}</p>
        </div>
        <br>
        <div class="text-white">
            <h3 class="text-lg font-bold">Launch Site</h3>
            <p>Location: {{launch.launch_site.site_name_long}}</p>
        </div>
        <br>
        <div class="text-white">
            <h3 class="text-lg font-bold">Launch Details</h3>
            <p>{{launch.details}}</p>
        </div>
    </div>
</div>
